scheme1.innerHTML = scheme_svg;
scheme2.innerHTML = scheme_svg;
phone.innerHTML = phone_svg;
phone1.innerHTML = phones_svg;
cloud.innerHTML = cloud_svg;

function getArray(array) {
    for (var i = 0; i < array.length; i++) {
        array[i] = document.getElementById(array[i]);
    }
}

function timeActive(time) {
    setTimeout(function() { active = false }, time)
}
ls[0].innerHTML = rect;
ls[1].innerHTML = rect2;

getArray(texts);
getArray(speech);
lines.forEach(function(line) {
    line.innerHTML = ripple;
});
var speech1_anim, speech2_anim, speech3_anim;

function speech_fn(i) {
    speech[i].classList.remove('off')
    setTimeout(function() { speech[i].classList.add('off') }, 1500)
}

function random(min, max) {
    var rand = min + Math.random() * (max - min)
    rand = Math.round(rand);
    return rand;
}

var debag = false;

function dd() {
    if (debag) {
        var s = document.getElementById('step');
        s.innerHTML = step;
    }
}

function next() {
    if (step < 9 && !active) {
        active = true;
        dd();
        switch (step) {
            case 1:
                phones.classList.remove('micro_opacity');
                cloud.classList.remove('micro_opacity');
                break;
            case 2:
                scheme1.classList.add('green');
                setTimeout(function() {
                    scheme1.classList.remove('micro_opacity');
                    lines[0].classList.remove('micro_hide');
                    speech_fn(0);
                    speech1_anim = setInterval(function() { speech_fn(0) }, 2300);
                }, 500);
                scheme1.classList.remove('shake');
                timeActive(1000);
                break;
            case 3:
                clearInterval(speech1_anim);
                speech[0].classList.add('off');
                setTimeout(function() {
                    speech_fn(3);
                    speech1_anim = setInterval(function() {
                        speech_fn(0)
                    }, 2300);
                }, 500)
                scheme1.classList.add('shake');
                timeActive(2000);
                break;
            case 4:
                scheme1.classList.remove('shake');
                scheme2.classList.add('green');
                setTimeout(function() {
                    scheme2.classList.remove('micro_opacity');
                    speech2_anim = setInterval(function() {
                        speech_fn(1)
                    }, 2300);
                }, 500)
                setTimeout(function() {
                    lines[1].classList.remove('micro_hide');
                    texts[0].classList.remove('micro_hide');
                }, 700)
                timeActive(1000);
                break;
            case 5:
                scheme1.classList.add('red');
                clearInterval(speech1_anim);
                lines[0].classList.add('micro_hide');
                texts[0].classList.add('micro_hide');
                break;
            case 6:
                phone.classList.add('green');

                setTimeout(function() {
                    phone.classList.remove('micro_opacity');
                    lines[2].classList.remove('micro_hide');
                    texts[1].classList.remove('micro_hide');
                    document.querySelector('#micro_phone #b').style.opacity = 1;
                    document.querySelector('#micro_phone #c').style.opacity = 1;
                    document.querySelector('#micro_phone #d').style.opacity = 1;
                    speech[2].childNodes[1].innerHTML = random(10, 99) + '.' + random(1000, 9999) + '<br>-' + random(10, 99) + '.' + random(100, 999);
                    speech_fn(2)
                    speech3_anim = setInterval(function() {
                        speech[2].childNodes[1].innerHTML = random(10, 99) + '.' + random(1000, 9999) + '<br>-' + random(10, 99) + '.' + random(100, 999);
                        speech_fn(2)
                    }, 2300);
                }, 500)
                timeActive(1000);
                break;
            case 7:
                document.getElementById('text3').classList.remove('micro_hide');
                break;
            case 8:
                document.getElementById('text_wifi').classList.remove('micro_hide');
                document.querySelector('#phones #phone1 #d').classList.remove('micro_hide');
                setTimeout(function() {
                    document.querySelector('#phones #phone1 #d').classList.add('micro_hide');
                }, 1000)
                setTimeout(function() {
                    document.querySelector('#phones #phone1 #b').classList.remove('micro_hide');
                }, 1500)
                setTimeout(function() {
                    document.querySelector('#phones #phone1 #b').classList.add('micro_hide');
                }, 2500)
                phone2_anim = setInterval(function() {
                    document.querySelector('#phones #phone1 #d').classList.remove('micro_hide');
                    setTimeout(function() {
                        document.querySelector('#phones #phone1 #d').classList.add('micro_hide');
                    }, 1000)
                    setTimeout(function() {
                        document.querySelector('#phones #phone1 #b').classList.remove('micro_hide');
                    }, 1500)
                    setTimeout(function() {
                        document.querySelector('#phones #phone1 #b').classList.add('micro_hide');
                    }, 2500)
                }, 3000)
                timeActive(3000);
                break;
        }
        timeActive(500);
        step++;
    }
}

function prev() {
    dd();
    if (step > 1 && !active) {
        switch (step) {
            case 2:
                phones.classList.add('micro_opacity');
                cloud.classList.add('micro_opacity');
                break;
            case 3:
                scheme1.classList.remove('green');
                clearInterval(speech1_anim);
                setTimeout(function() {
                    scheme1.classList.add('micro_opacity');
                    lines[0].classList.add('micro_hide');
                }, 500)
                lines[0].classList.add('micro_hide');
                timeActive(1000);
                break;
            case 4:
                scheme1.classList.add('shake');
                break;
            case 5:
                active = true;
                scheme1.classList.remove('shake');
                scheme2.classList.remove('green');
                clearInterval(speech2_anim);
                texts[0].classList.add('micro_hide');
                lines[1].classList.add('micro_hide');
                setTimeout(function() { scheme2.classList.add('micro_opacity'); }, 500)
                break;
            case 6:
                scheme1.classList.remove('red');
                setTimeout(function() {
                    speech_fn(0);
                    lines[0].classList.remove('micro_hide');
                    texts[0].classList.remove('micro_hide');
                    speech1_anim = setInterval(function() { speech_fn(0) }, 2300);
                }, 500);
                timeActive(1000);
                break;
            case 7:
                phone.classList.remove('green');

                setTimeout(function() {
                    document.querySelector('#micro_phone #b').style.opacity = 0;
                    document.querySelector('#micro_phone #c').style.opacity = 0;
                    document.querySelector('#micro_phone #d').style.opacity = 0;
                    phone.classList.add('micro_opacity');
                    lines[2].classList.add('micro_hide');
                    texts[1].classList.add('micro_hide');
                }, 500);
                clearInterval(speech3_anim);
                timeActive(1000);
                break;
            case 8:
                document.getElementById('text3').classList.add('micro_hide');
                break;
            case 9:
                document.getElementById('text_wifi').classList.add('micro_hide');

                clearInterval(phone2_anim);
                document.querySelector('#phones #phone1 #d').classList.add('micro_hide');
                document.querySelector('#phones #phone1 #b').classList.add('micro_hide');
                break;

        }
        timeActive(500);
        step--;
    }
}